import React from 'react';
import Select from 'react-select';

function Booking() {

    const [date, setDate] = useState('');
    const [time, setTime] = useState('');
    const [guests, setGuests] = useState('');
    const [occasion, setOccasion] = useState('');

  const guestOptions = [
    { value: '1', label: '1' },
    { value: '2', label: '2' },
    { value: '3', label: '3' },
    { value: '4', label: '4' },
    { value: '5', label: '5' },
    { value: '6', label: '6' },
    { value: '7', label: '7' },
    { value: '8', label: '8' },
    { value: '9', label: '9' },
    { value: '10', label: '10' },
  ];

  const timeOptions = [
    { value: '17:00', label: '17:00' },
    { value: '18:00', label: '18:00' },
    { value: '19:00', label: '19:00' },
    { value: '20:00', label: '20:00' },
    { value: '21:00', label: '21:00' },
    { value: '22:00', label: '22:00' },
  ];

  const occasionOptions = [
    { value: 'Birthday', label: 'Birthday' },
    { value: 'Anniversary', label: 'Anniversary' },
  ];

  const customStyles = {
    control: (provided) => ({
      ...provided,
      width: '400px',
      padding: '10px',
      borderRadius: '5px',
      marginTop: '5px',
      backgroundColor: 'white',
      minWidth: '400px',
    }),
    option: (provided) => ({
      ...provided,
      color: 'black',
    }),
  };

  return (
    <form className="booking-form">
      <label htmlFor="res-date"><strong>Choose date</strong></label>
      <input type="date" id="res-date" value={date} onChange={e => setDate(e.target.value)} />

      <label htmlFor="res-time"><strong>Choose time</strong></label>
      <Select options={timeOptions} styles={customStyles} value={time} onChange={selectedOption => setTime(selectedOption)} />

      <label htmlFor="guests"><strong>Number of guests</strong></label>
      <Select options={guestOptions} styles={customStyles} value={guests} onChange={selectedOption => setGuests(selectedOption)} />

      <label htmlFor="occasion"><strong>Occasion</strong></label>
      <Select options={occasionOptions} styles={customStyles} value={occasion} onChange={selectedOption => setOccasion(selectedOption)} />

      <input type="submit" value="Make Your reservation" />
    </form>
  );
}           

export default Booking;
