import React, { useReducer, useEffect } from 'react';
import { Routes, Route } from 'react-router-dom';
import Homepage from './Homepage';
import Header from './Header'; 
import AboutPage from './AboutPage';
import BookingPage from './BookingPage';
import { fetchAPI, submitAPI } from './mockAPI';

// Define an action type for setting available times
const SET_TIMES = 'SET_TIMES';

// The reducer is responsible for setting the available times
const updateTimes = (state, action) => {
  switch (action.type) {
    case SET_TIMES:
      return action.payload;
    default:
      return state;
  }
};

function Main() {

  async function initializeTimes() {
    const today = new Date().toISOString().split('T')[0]; // Convert the date to the required format

    // Fetch the available times for today's date
    let times = await fetchAPI(today);

    // If fetchAPI returns an error (i.e., there are no available times), times will be undefined
    // In this case, initialize times as an empty array
    if (!times) {
      times = [];
    }

    return times.map(time => ({ value: time, label: time }));
  }

  const updateTimes = async (state, action) => {
    const times = await fetchAPI(action);
    return times.map(time => ({ value: time, label: time }));
  };


  const [availableTimes, dispatch] = useReducer(updateTimes, []);

  useEffect(() => {
    const today = new Date().toISOString().split('T')[0]; // Convert the date to the required format

    // Fetch the available times for today's date
    fetchAPI(today)
      .then(times => times.map(time => ({ value: time, label: time })))
      // Dispatch an action to set the available times
      .then(options => dispatch({ type: SET_TIMES, payload: options }));
  }, []);

  return (
    <Routes>
      <Route path="/" element={<><Header /><Homepage /></>} />
      <Route path="/about" element={<AboutPage />} />
      <Route path="/reservations" element={<BookingPage availableTimes={availableTimes} dispatch={dispatch}  />} />
    </Routes>
  );
}

export default Main;
